CREATE TABLE Person(PID INT8 NOT NULL PRIMARY KEY,Firstname VARCHAR(50) NOT NULL, Lastname VARCHAR(50) NOT NULL, Email VARCHAR(50));
CREATE TABLE Project(PrID INT8 NOT NULL PRIMARY KEY, Title VARCHAR(50) NOT NULL,CreatedAt date NOT NULL);
CREATE TABLE Issue(IID INT8 NOT NULL PRIMARY KEY, Title VARCHAR(50) NOT NULL,CreatedAt date NOT NULL, State VARCHAR(50) NOT NULL , StateReason VARCHAR(50) NOT NULL, PrID INT8 NOT NULL,CONSTRAINT "FK2.PrID" FOREIGN KEY (PrID) REFERENCES Project (PrID) ON DELETE CASCADE);
CREATE TABLE Person_Project(PID INT8 NOT NULL, PrID INT8 NOT NULL, PRIMARY KEY (PID,PrID), CONSTRAINT "FK1.PID" FOREIGN KEY (PID) REFERENCES Person (PID) ON DELETE CASCADE, CONSTRAINT "FK2.PrID" FOREIGN KEY (PrID) REFERENCES Project (PrID) ON DELETE CASCADE);
CREATE TABLE Person_Issue(PID INT8 NOT NULL, IID INT8 NOT NULL, PRIMARY KEY (PID,IID), CONSTRAINT "FK1.PID" FOREIGN KEY (PID) REFERENCES Person (PID) ON DELETE CASCADE, CONSTRAINT "FK2.IID" FOREIGN KEY (IID) REFERENCES Issue (IID) ON DELETE CASCADE)